#include  "test.h"

/*
 * This file implements the following C extern function:
 *
 * bool verify_ipv4_checksum(in IPv4_h iphdr)
 *
 */


/**
 * This function implements method to verify IP checksum.
 * @param iphdr Structure representing IP header. The IP header is generated by the P4 compiler and defined in test.h.
 * @return True if checksum is correct.
 */
u8 verify_ipv4_checksum(const struct IPv4_h iphdr)
{
    u8 correct = 0;
    u32 checksum = htons(((u16) iphdr.version << 12) | ((u16) iphdr.ihl << 8) | (u16) iphdr.diffserv);
    checksum += htons(iphdr.totalLen);
    checksum += htons(iphdr.identification);
    checksum += htons(((u16) iphdr.flags << 13) | iphdr.fragOffset);
    checksum += htons(((u16) iphdr.ttl << 8) | (u16) iphdr.protocol);
    checksum += htons(iphdr.hdrChecksum);
    u32 srcAddr = htonl(iphdr.srcAddr);
    u32 dstAddr = htonl(iphdr.dstAddr);
    checksum += (srcAddr >> 16) + (u16) srcAddr;
    checksum += (dstAddr >> 16) + (u16) dstAddr;

    u16 res = __constant_ntohs(~((checksum & 0xFFFF) + (checksum >> 16)));

    if (res == 0)
        correct = 1;
    return correct;
}


